pragma solidity ^0.5.0;

import {ECDSA} from "./ECDSA.sol";

contract tdryecdsa2{

	//uint length;

	function getResult() public pure returns(uint sum){
		uint a = 1;
		uint b = 2;
		sum = a + b;
		return sum;
	}


	function verify( bytes memory signature, string memory _msg, uint _revision) public view returns (bool ) {
		//require(hash == computeMsgHash(_msg, _revision), "The hash is not produced from this message !!!");
		bytes32 hash = computeMsgHash(_msg, _revision);
		address _sender = ECDSA.recover(hash, signature);
		if (_sender == msg.sender) {
			return true;
		} else {
			return false;
		}

	}


	function computeMsgHash(string memory my_msg, uint revision) public pure returns (bytes32 keccakHash) {
		bytes32 hash = keccak256(abi.encodePacked(my_msg, revision));
		keccakHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash ));
		//_length = my_msg.length;
		return keccakHash;
	}





}
